import React, { useState } from "react";
import "../Website.css";
import "./Gallery.css";

const Gallery = () => {
  const [activeFilter, setActiveFilter] = useState("all");
  const [selectedImage, setSelectedImage] = useState(null);

  const galleryCategories = [
    { id: "all", label: "All" },
    { id: "campus", label: "Campus" },
    { id: "events", label: "Events" },
    { id: "sports", label: "Sports" },
    { id: "cultural", label: "Cultural" },
    { id: "academic", label: "Academic" },
    { id: "achievements", label: "Achievements" },
  ];

  const galleryItems = [
    {
      id: 1,
      category: "campus",
      image:
        "data:image/jpeg;base64,",
      title: "Modern Campus Building",
      description: "Our state-of-the-art main building",
      date: "2023",
    },
    // Add more gallery items for each category
  ];

  const handleImageClick = (image) => {
    setSelectedImage(image);
  };

  const closeModal = () => {
    setSelectedImage(null);
  };

  return (
    <div className="website-container">
      {/* Banner Section */}
      <section className="page-banner gallery-banner">
        <div className="banner-overlay">
          <h1>Our Gallery</h1>
          <p>Capturing Moments, Creating Memories</p>
        </div>
      </section>

      {/* Gallery Introduction */}
      <section className="gallery-intro">
        <div className="intro-content">
          <h2>Welcome to Our School Gallery</h2>
          <p>
            Explore our vibrant collection of photographs showcasing the diverse
            aspects of life at Excellence Academy. From academic achievements to
            sporting victories, cultural celebrations to campus innovations.
          </p>
        </div>
      </section>

      {/* Featured Gallery */}
      <section className="featured-gallery">
        <div className="section-header">
          <h2>Featured Highlights</h2>
          <div className="section-divider"></div>
        </div>
        <div className="featured-slider">
          {/* Add featured gallery items */}
          <div className="featured-item">
            <img
              src="data:image/jpeg;base64,"
              alt="Annual Day Celebration"
            />
            <div className="featured-overlay">
              <h3>Annual Day 2023</h3>
              <p>A grand celebration of talent and achievement</p>
            </div>
          </div>
          {/* Add more featured items */}
        </div>
      </section>

      {/* Main Gallery Section */}
      <section className="main-gallery">
        <div className="section-header">
          <h2>Photo Gallery</h2>
          <div className="section-divider"></div>
        </div>

        {/* Gallery Filters */}
        <div className="gallery-filters">
          <div className="filter-buttons">
            {galleryCategories.map((category) => (
              <button
                key={category.id}
                className={`filter-button ${
                  activeFilter === category.id ? "active" : ""
                }`}
                onClick={() => setActiveFilter(category.id)}
              >
                {category.label}
              </button>
            ))}
          </div>
        </div>

        {/* Gallery Grid */}
        <div className="gallery-grid">
          {galleryItems
            .filter(
              (item) => activeFilter === "all" || item.category === activeFilter
            )
            .map((item) => (
              <div
                key={item.id}
                className="gallery-item"
                onClick={() => handleImageClick(item)}
              >
                <img src={item.image} alt={item.title} />
                <div className="gallery-overlay">
                  <h3>{item.title}</h3>
                  <p>{item.description}</p>
                  <span className="gallery-date">{item.date}</span>
                </div>
              </div>
            ))}
        </div>

        {/* Load More Button */}
        <button className="load-more">Load More</button>
      </section>

      {/* Video Gallery Section */}
      <section className="video-gallery">
        <div className="section-header">
          <h2>Video Gallery</h2>
          <div className="section-divider"></div>
        </div>
        <div className="video-grid">
          <div className="video-item">
            <div className="video-thumbnail">
              <img
                src="data:image/jpeg;base64,"
                alt="School Annual Day"
              />
              <div className="play-button">
                <i className="fas fa-play"></i>
              </div>
            </div>
            <div className="video-info">
              <h3>Annual Day Celebrations 2023</h3>
              <p>Watch the highlights of our grand annual day celebration</p>
            </div>
          </div>
          {/* Add more video items */}
        </div>
      </section>

      {/* Virtual Tour Section */}
      <section className="virtual-tour">
        <div className="section-header">
          <h2>Virtual Campus Tour</h2>
          <div className="section-divider"></div>
        </div>
        <div className="tour-container">
          <div className="tour-preview">
            <img
              src="data:image/jpeg;base64,"
              alt="Virtual Tour"
            />
            <div className="tour-overlay">
              <h3>Experience Our Campus</h3>
              <p>Take a 360° virtual tour of our world-class facilities</p>
              <button className="start-tour-btn">
                <i className="fas fa-vr-cardboard"></i> Start Tour
              </button>
            </div>
          </div>
        </div>
      </section>

      {/* Image Modal */}
      {selectedImage && (
        <div className="gallery-modal" onClick={closeModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <button className="modal-close" onClick={closeModal}>
              ×
            </button>
            <img
              src={selectedImage.image}
              alt={selectedImage.title}
              className="modal-image"
            />
            <div className="modal-caption">
              <h3>{selectedImage.title}</h3>
              <p>{selectedImage.description}</p>
            </div>
            <button className="modal-nav modal-prev">‹</button>
            <button className="modal-nav modal-next">›</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Gallery;
